# ввод данных констант
TAX = 0.15
PRICE_FREE = 200

packages_rooms_mod = dict()                                                                                     # создадим словарь, в который будем класть адреса, но уже полные с квартирами и суммами покупок
for i in packages_rooms:                                                                                        # мы не знаем сколько покупок на каждую квартиру, поэтому в первом цикле просто заполним словарь итоговыми суммами покупок
    address = "Адрес: " + i[0] + ", " + str(i[1]) + "; Пошлина составила:"
    if address not in packages_rooms_mod.keys():
        packages_rooms_mod[address] = i[-1]
    else:
        packages_rooms_mod[address] += i[-1]

packages_rooms_result = dict()                                                                                  # в этом блоке мы создадим новый словарь и заполним его суммой пошлины для каждого адреса
for a,b in packages_rooms_mod.items():
    if b > PRICE_FREE:
        b = round(((b - PRICE_FREE) * TAX), 2)
    else:
        b = 0
    packages_rooms_result[a] = b

for i in packages_rooms_result.items():
    print(i)